	public static int finalFloor(String str) {
		int floor = 0;
		for (int i = 0; i < str.length(); i++) {
			if (str.charAt(i) == '(') {
				floor ++;
			}
			else {
				floor --;
			}
			if (floor == -1) {
				return i + 1;
			}
		}
		return -1;
	}

